import styled from 'styled-components'

export const ContainerFooter = styled.section`

    width: 100%;
    height: 10vh;
    display: flex;
    margin: auto;
    text-align: center;
    align-items: center;
    justify-content: space-between;
    background-color: #d6d6d6;
`

export const BoxParagrf = styled.div`

    width: 40%;
    display: flex;
    text-align: center;
    align-items: center;
    justify-content: flex-start;
    background-color: none;
`

export const ParagraFooter = styled.p`

    width: 60%;
    display: flex;
    text-align: center;
    align-items: center;
    justify-content: center;
    font-family: sans-serif;
    font-size: 1.1em;
    font-weight: 750;
    color: #8A038C;
`

export const ContainerSocialMidias = styled.section`

    width: 40%;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    background-color: none;
`

export const BoxFigFooter = styled.figure`

    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: none;
`

export const ImageFooter = styled.img`

    width: 2rem;
    display: flex;
    margin: 0 auto;
    border-radius: 50px;
    border: -moz-box-shadow: 0px 20px 25px #8A038C;
    -webkit-box-shadow: 0px 0px 12px #8A038C;
    cursor: pointer;
`
